import { Button, Container, Dialog, DialogTitle, Input, TextField, Typography } from '@mui/material';
import { Stack } from '@mui/system';
import axios from 'axios';
import React, { useState } from 'react';
import BaseAdminView from '../BaseAdminView/BaseAdminView';
import DataTable from "../DataTable/DataTable";
import { TOKEN_KEY } from '../../api/auth';
import { addPaymentMethod } from '../../api/admin';

const tableHeaders = ["image", "name", "Status", "Active"];

const AddPaymentMethod = (props) => {
  const {open, setOpen} = props;
  const [imageDataUrl, setImageDataUrl] = useState("");
  const [name, setName] = useState("");

  const handleChangeName = (value) => {
    setName(value);
  }

  const handleClose = () => {
    setOpen(false);
  };

  const submitData = () => {

      const data = {
        name : name,
        image : imageDataUrl
      };
      console.log(imageDataUrl);

      addPaymentMethod(name, image)
      .then(function (response, data) {
        //handle success
        console.log(response, data);
        handleClose();
      })
      .catch(function (response) {
        //handle error
        console.log(response);


      })

  };
  return(
    <Dialog 
      open={open}
      onClose={handleClose}
      >
      <DialogTitle fontFamily='Poppins' textAlign="center" >Add New Payment Method</DialogTitle>
      <Container 
      sx={{
        display:'flex',
        flexDirection: 'column',
        width:'100%',
        marginBottom: '20px',
        gap:'15px',

      }}>
      {/* <Typography>Name</Typography> */}
      <TextField id="name" type="text" label="Name" onChange={(e) => handleChangeName(e.target.value)}/>
      <Input id='imageupload' className='hidden' type='file' accept='image/*'
      onChange={(e) => {
        const tempImage = e.target.files[0];

        let reader = new FileReader();
        reader.onload = () => {
          console.log('BASE64', reader.result)
          setImageDataUrl(reader.result)
        }
        console.log(tempImage);
        reader.readAsDataURL(tempImage);
      }}/>

      <Button 
      variant="contained"
      color="secondary"
      display="flex"
      justifyContent="center"
      sx={{
        mb:'20px'
      }}
      >Chosee Image</Button>

      <Button 
      variant="contained"
      color="secondary"
      display="flex"
      justifyContent="center"
      sx={{
          width:'100px',
          height:'40px',

      }}
      onClick={submitData}
      >Save</Button>
      </Container>

    </Dialog>

  )
};

const AdminPaymentMethod = () => {
  const [open, setOpen] = useState(false);
  const [userRows, setUsersRows] = useState([]);

  const handleClickOpen = () => {
    setOpen(true);
  };

  return (
    <BaseAdminView>
        <Button
        variant="contained"
        color="secondary"
        display="flex"
        sx={{
            left: '30px',

            height:'40px',
            mt:'20px',
            mb:'10px'

        }}
        onClick={handleClickOpen}
        >Add Payment Method</Button>
        <AddPaymentMethod open={open} setOpen={setOpen}/>
        <DataTable headers={tableHeaders}  rows={userRows}/>

    </BaseAdminView>
  )
}

export default AdminPaymentMethod
