const setIDR = (price) =>{
    let arrprice = price.toString().split("").reverse().join("");
    let result = "";
    for (let i = 0; i < arrprice.length; i+=3) {
        if (i+3 < arrprice.length) {
            result += arrprice.substring(i, i+3) + ".";
        }
        else {
            result += arrprice.substring(i, i+3);
        }
    }
    return result.split("").reverse().join("");
};

export { setIDR };
