import React, { useEffect, useState } from "react";

import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import Typography from "@mui/material/Typography";

import { useTheme } from "@mui/material/styles";

import { useParams, Link } from "react-router-dom";

import { getListMenuClass } from "../../api/soup";

import Footer from "../Footer/Footer";

const ClassCard = (props) => {
  const { course } = props;

  const theme = useTheme();
  const gray3 = theme.palette.gray3.main;

  const detailPath = `/detailkelaspage/${course.course_id}`;

  return (
    <Stack spacing={2} sx={{ width: "350px" }}>
      <Link to={detailPath}>
        <img
          src={`data:image;base64,${course.image_content}`}
          width="350"
          height="233"
        />
      </Link>
      <Stack spacing={1} sx={{ textAlign: "left", paddingBottom: "24px" }}>
        <Stack spacing="4px">
          <Typography paragraph={true} sx={{ margin: "0", color: gray3 }}>
            {course.category_name}
          </Typography>
          <Typography
            variant="h6"
            component="h6"
            color="primary"
            sx={{ height: "70px", fontWeight: "600" }}
          >
            <Link to={detailPath}>{course.name}</Link>
          </Typography>
        </Stack>
        <Typography
          paragraph={true}
          color="secondary"
          sx={{ fontSize: "20px", fontWeight: "600" }}
        >
          IDR {course.price.toLocaleString("id")}
        </Typography>
      </Stack>
    </Stack>
  );
};

const ListMenuClass = (props) => {
  const { courses } = props;

  return (
    <Stack spacing={10} sx={{ width: "1098px", margin: "0 auto" }}>
      <Typography
        variant="h4"
        component="h2"
        color="primary"
        sx={{ textAlign: "center", fontWeight: "600" }}
      >
        Another menu in this class
      </Typography>
      <Box
        sx={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)",
          gap: "40px 24px",
        }}
      >
        {courses.map((course, key) => (
          <ClassCard key={key} course={course} />
        ))}
      </Box>
    </Stack>
  );
};

const ListMenuKelasPage = () => {
  const { categoryId } = useParams();
  const [data, setData] = useState(null);

  const theme = useTheme();
  const gray1 = theme.palette.gray1.main;

  useEffect(() => {
    setData(null);
    getListMenuClass(categoryId)
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [categoryId]);

  if (!data) return null;

  return (
    <Stack spacing="141px">
      <div>
        <Box sx={{ marginBottom: "46px" }}>
          <img src="/listmenukelaspageimagetop.svg" style={{ width: "100%" }} />
        </Box>
        <Stack
          spacing={2}
          sx={{
            width: "1140px",
            margin: "0 auto",
            marginBottom: "132px",
            textAlign: "left",
          }}
        >
          <Typography variant="h5" component="h1" sx={{ fontWeight: "600" }}>
            {data.category.name}
          </Typography>
          <Typography paragraph={true} sx={{ color: gray1 }}>
            {data.category.description}
          </Typography>
        </Stack>
        <ListMenuClass courses={data.courses} />
      </div>
      <Footer />
    </Stack>
  );
};

export default ListMenuKelasPage;
export { ListMenuClass };
