import React, { useState } from 'react';
import BaseAdminView from '../BaseAdminView/BaseAdminView';
import DataTable from "../DataTable/DataTable";

const tableHeaders = ["image", "name", "Status", "Active"];

const AddPaymentMethod = (props) => {
  const {open, setOpen} = props;
  const [imageRaw, setImageRaw] = useState(null);
  const [imageDataUrl, setImageDataUrl] = useState(null);

  const handleClose = () => {
    setOpen(false);
  };
  return(
    <Dialog 
      open={open}
      onClose={handleClose}
      >
      <DialogTitle fontFamily='Poppins' textAlign="center" >Add New Payment Method</DialogTitle>
      <Container 
      sx={{
        display:'flex',
        flexDirection: 'column',
        width:'100%',
        marginBottom: '20px',
        gap:'15px',

      }}>
      {/* <Typography>Name</Typography> */}
      <TextField id="name" type="text" label="Name" />
      <Input id='imageupload' className='hidden' type='file' 
      onChange={() => {
        // const tempImage = e.target.files[0];
        // setImageRaw(tempImage);
        // setImageObjectUrl(URL.createObjectURL(tempImage));

