      }}/>
      <Button 

      variant="contained"
      color="secondary"
      display="flex"
      justifyContent="center"
      sx={{
        mb:'20px'
      }}
      >Chosee Image</Button>

      <Button 
      variant="contained"
      color="secondary"
      display="flex"
      justifyContent="center"
      sx={{
          width:'100px',
          height:'40px',

      }}
      >Save</Button>
      </Container>

    </Dialog>

  )
};

const AdminPaymentMethod = () => {
  const [open, setOpen] = useState(false);
  const [userRows, setUsersRows] = useState([]);

  const handleClickOpen = () => {
    setOpen(true);
  };

  return (
    <BaseAdminView>
        <Button
        variant="contained"
        color="secondary"
        display="flex"
        sx={{
            left: '30px',
            width:'133px',
            height:'40px',
            mt:'20px',
            mb:'10px'

        }}
        onClick={handleClickOpen}
        >Add User</Button>
        <AddPaymentMethod open={open} setOpen={setOpen}/>
        <DataTable headers={tableHeaders}  rows={userRows}/>

    </BaseAdminView>
  )
}

export default AdminPaymentMethod
