import React, { useState } from "react";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";
import TextField from "@mui/material/TextField";
import Typography from "@mui/material/Typography";

import { useTheme } from "@mui/material/styles";
import { useNavigate, Link } from "react-router-dom";

import useAuth from "../../hook/useAuth";

import { login } from "../../api/auth";

const LoginPage = () => {
  const theme = useTheme();
  const primaryColor = theme.palette.primary.main;
  const gray2 = theme.palette.gray2.main;
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const { storeLoginData } = useAuth();

  // TODO: validate email and password format
  const tryLogin = () => {
    login(email, password)
      .then((res) => {
        storeLoginData(res.data);
        navigate("/");
      })
      .catch((err) => {
        const msg = err?.response?.data;
        alert(msg);
      });
  };

  return (
    <Stack
      spacing="60px"
      sx={{ width: "616px", margin: "0 auto", marginTop: "60px" }}
    >
      <Stack spacing={5} sx={{ textAlign: "left" }}>
        <Stack spacing={2}>
          <Typography
            variant="h5"
            component="h1"
            sx={{ color: primaryColor, fontWeight: "500" }}
          >
            Welcome Back! Cheff
          </Typography>
          <Typography paragraph={true} sx={{ color: gray2 }}>
            Please login first
          </Typography>
        </Stack>
        <Stack spacing={3}>
          <TextField
            type="email"
            label="Email"
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            type="password"
            label="Password"
            onChange={(e) => setPassword(e.target.value)}
          />
          <Typography paragraph={true} sx={{ color: gray2 }}>
            Forgot Password? <Link to="/resetpasswordemail">Click Here</Link>
          </Typography>
        </Stack>
        <Button
          variant="contained"
          color="secondary"
          sx={{
            width: "140px",
            alignSelf: "flex-end",
            borderRadius: "8px",
            color: primaryColor,
            textTransform: "none",
          }}
          onClick={tryLogin}
        >
          Login
        </Button>
      </Stack>
      <Typography
        paragraph={true}
        sx={{
          textAlign: "center",
        }}
      >
        Dont have account? <Link to="/register">Sign Up here</Link>
      </Typography>
    </Stack>
  );
};

export default LoginPage;
